mapper.select:
select its.id as its_id, its.name as its_name, its.description as its_description, its.apply_type as its_apply_type, its.default_issue_type_id as its_default_issue_type_id, its.organization_id as its_organization_id, its.object_version_number as its_object_version_number, itr.issue_type_id as itr_issue_type_id, itr.name as itr_issue_type_name, itr.icon as itr_icon, itr.description as itr_description, itr.colour as itr_colour, itr.organization_id as itr_organization_id, itr.type_code as itr_type_code, itr.is_initialize as itr_is_initialize, pci.project_id as pci_project_id from fd_issue_type_scheme its left join ( select itsc.scheme_id, itsc.issue_type_id, it.name, it.icon, it.description, it.colour, it.organization_id, it.type_code, it.is_initialize from fd_issue_type_scheme_config itsc, issue_type it where itsc.issue_type_id = it.id ) itr on its.id = itr.scheme_id left join ( select pc.scheme_id, pc.project_id from fd_project_config pc where pc.scheme_type = 'scheme_issue_type' ) pci on pci.scheme_id = its.id where its.organization_id = #{organizationId} and its.id in
#{issueTypeSchemeId}
order by its.id desc
