SELECT IFNULL(a.new_string,0) as new_value, d.issue_id, #{sprintDTO.startDate} AS date, 'startSprint' AS type, 1 AS statistical, 0 AS old_value, CONCAT_WS( '-', api.project_code, d.issue_num ) AS issue_num, CONCAT_WS( '-', api2.project_code, cd.issue_num ) AS parent_issue_num, cd.issue_id AS parent_issue_id FROM ( SELECT ai.issue_id, ai.issue_num, ai.project_id, ai.parent_issue_id FROM agile_issue ai WHERE ai.project_id = #{sprintDTO.projectId} and ai.issue_id IN
#{issueId}
) d LEFT JOIN ( select adl3.new_string,adl3.issue_id,adl3.creation_date from agile_data_log adl3 where adl3.project_id = #{sprintDTO.projectId} and adl3.field = #{field} and adl3.log_id in (SELECT MAX(adl.log_id) as log_id FROM agile_data_log adl WHERE adl.project_id = #{sprintDTO.projectId} and adl.field = #{field} AND adl.creation_date < #{sprintDTO.startDate} AND adl.issue_id IN
#{issueId}
GROUP BY adl.issue_id) ) a ON a.issue_id = d.issue_id LEFT JOIN agile_project_info api ON api.project_id = d.project_id left JOIN agile_issue cd on d.parent_issue_id = cd.issue_id LEFT JOIN agile_project_info api2 ON api2.project_id = cd.project_id

