select *,(pieData.value/#{total})*100 as percent from ( SELECT
ai.${fieldName} AS type_name,
other.is_completed AS type_name, ( CASE other.is_completed WHEN 0 THEN '' WHEN 1 THEN '' ELSE NULL END) AS name,
other.${fieldName} AS type_name, otherName.name as name,
other.${fieldName} AS type_name, otherName.sprint_name as name,
other.${fieldName} AS type_name, otherName.label_name as name,
count( * ) AS value FROM agile_issue ai
LEFT JOIN agile_issue_status other on ai.status_id = other.status_id and other.project_id = ai.project_id
LEFT JOIN agile_component_issue_rel other on ai.issue_id = other.issue_id LEFT JOIN agile_issue_component otherName on other.${fieldName} = otherName.${fieldName}
LEFT JOIN agile_version_issue_rel other on ai.issue_id = other.issue_id and other.relation_type = 'fix' LEFT JOIN agile_product_version otherName on other.${fieldName} = otherName.${fieldName}
LEFT JOIN agile_issue_sprint_rel other on ai.issue_id = other.issue_id LEFT JOIN agile_sprint otherName on other.${fieldName} = otherName.${fieldName}
LEFT JOIN agile_label_issue_rel other on ai.issue_id = other.issue_id LEFT JOIN agile_issue_label otherName on other.${fieldName} = otherName.${fieldName}
WHERE ai.project_id = #{projectId} AND ai.apply_type = 'agile'
and ai.creation_date >= #{startDate} and ai.creation_date <= #{endDate}
and ai.issue_id in (select aisr.issue_id from agile_issue_sprint_rel aisr where aisr.sprint_id = #{sprintId})
and ai.issue_id in (select avir.issue_id from agile_version_issue_rel avir where avir.version_id = #{versionId} and avir.relation_type = 'fix')
GROUP BY
ai.${fieldName}
other.is_completed
other.${fieldName}
)pieData ORDER BY pieData.value DESC

SELECT t2.group_day, COUNT(adl3.issue_id) AS issue_completed_count FROM agile_data_log adl3, ( SELECT t1.group_day AS group_day, adl.issue_id AS issue_id, MAX(adl.log_id) AS log_id FROM ( SELECT DISTINCT ( DATE_FORMAT( last_update_date, '%Y-%m-%d' ) ) group_day FROM agile_data_log WHERE project_id = #{projectId} AND issue_id IN (
SELECT issue_id FROM agile_issue WHERE epic_id = #{id} and apply_type = 'agile'
SELECT aviro.issue_id FROM agile_version_issue_rel aviro, agile_issue aio WHERE aviro.version_id = #{id} and aviro.issue_id = aio.issue_id and aio.apply_type = 'agile'
) AND field IN
('Epic Link', 'resolution')
('Fix Version', 'resolution')
ORDER BY group_day ) t1, agile_data_log adl WHERE t1.group_day >= DATE_FORMAT( adl.last_update_date, '%Y-%m-%d' ) AND adl.project_id = #{projectId}
AND adl.field = 'Epic Link' and (SELECT new_value FROM agile_data_log WHERE project_id = #{projectId} and t1.group_day >= DATE_FORMAT( last_update_date, '%Y-%m-%d' ) AND field = 'Epic Link' AND issue_id = adl.issue_id order BY log_id desc limit 1) = #{id}
AND adl.field = 'Fix Version' and (adl.old_value = #{id} OR adl.new_value = #{id})
AND adl.issue_id IN (
SELECT issue_id FROM agile_issue WHERE epic_id = #{id} and apply_type = 'agile'
SELECT avirr.issue_id FROM agile_version_issue_rel avirr, agile_issue air WHERE avirr.version_id = #{id} and t1.group_day >= DATE_FORMAT( avirr.last_update_date, '%Y-%m-%d' ) and avirr.issue_id = air.issue_id and air.apply_type = 'agile'
) GROUP BY t1.group_day, adl.issue_id ) t2, ( SELECT t10.group_day, t10.issue_id FROM agile_data_log adl9, ( SELECT t1.group_day, adl.issue_id, MAX(adl.log_id) AS log_id FROM ( SELECT DISTINCT ( DATE_FORMAT( last_update_date, '%Y-%m-%d' ) ) group_day FROM agile_data_log WHERE project_id = #{projectId} AND issue_id IN (
SELECT issue_id FROM agile_issue WHERE epic_id = #{id} and apply_type = 'agile'
SELECT avirp.issue_id FROM agile_version_issue_rel avirp, agile_issue aip WHERE avirp.version_id = #{id} and avirp.issue_id = aip.issue_id and aip.apply_type = 'agile'
) AND field IN
('Epic Link', 'resolution')
('Fix Version', 'resolution')
ORDER BY group_day ) t1, agile_data_log adl WHERE t1.group_day >= DATE_FORMAT( adl.last_update_date, '%Y-%m-%d' ) AND adl.project_id = #{projectId}
and (SELECT new_value FROM agile_data_log WHERE project_id = #{projectId} and t1.group_day >= DATE_FORMAT( last_update_date, '%Y-%m-%d' ) AND field = 'Epic Link' AND issue_id = adl.issue_id order BY log_id desc limit 1) = #{id}
AND adl.issue_id IN (
SELECT issue_id FROM agile_issue WHERE epic_id = #{id} and apply_type = 'agile'
SELECT avirh.issue_id FROM agile_version_issue_rel avirh, agile_issue aih WHERE avirh.version_id = #{id} and t1.group_day >= DATE_FORMAT( avirh.last_update_date, '%Y-%m-%d' ) and avirh.issue_id = aih.issue_id and aih.apply_type = 'agile'
) AND adl.field = 'resolution' GROUP BY t1.group_day, adl.issue_id ) t10 WHERE adl9.log_id = t10.log_id AND adl9.new_value IS NOT NULL ) t12 WHERE adl3.log_id = t2.log_id AND t12.group_day = t2.group_day AND t12.issue_id = t2.issue_id AND adl3.new_value = #{id} GROUP BY t2.group_day

SELECT aaa.priority_id, SUM( CASE WHEN aaa.is_completed = 1 THEN aaa.count ELSE 0 END ) AS done_count, SUM( aaa.count ) AS total_count FROM ( SELECT ai.priority_id, ais.is_completed, count( * ) AS count FROM agile_issue ai LEFT JOIN agile_issue ai2 ON ai.parent_issue_id = ai2.issue_id LEFT JOIN agile_issue_status ais ON ai.status_id = ais.status_id and ais.project_id = ai.project_id WHERE ai.project_id = #{projectId} AND ai.apply_type = 'agile' AND ( ai2.apply_type = 'agile' OR ai2.type_code IS NULL ) GROUP BY ai.priority_id, ais.is_completed ) aaa GROUP BY aaa.priority_id order by CASE aaa.priority_id
when #{priorityId} then #{index}
end

SELECT
a.*
FROM agile_data_log a
where a.field = 'resolution'
and a.project_id = #{projectId}
and a.issue_id = #{issueId}
and a.creation_date <= #{outDate}
order by a.creation_date desc LIMIT 1
