SELECT *
FROM fd_status
WHERE organization_id = #{organizationId}
and id = #{id}

select st.id as st_id, st.name as st_name, st.description as st_description, st.code as st_code, st.type as st_type, st.organization_id as st_organization_id, st.object_version_number as st_object_version_number, smddn.state_machine_id as smddn_state_machine_id, smddn.name as smddn_name, smddn.status as smddn_status from fd_status st left join (select smn.status_id, smn.state_machine_id, sm.name, sm.status from fd_state_machine_node smn, state_machine sm where smn.state_machine_id = sm.id union all select smnd.status_id, smnd.state_machine_id, sm.name, sm.status from fd_state_machine_node_draft smnd, state_machine sm where smnd.state_machine_id = sm.id) smddn on st.id = smddn.status_id where st.organization_id = #{organizationId} and st.id in
#{statusId}
order by st.id desc

